int findFirstUnique(vector<int> &v){

	unordered_map<int,int> um;
	unordered_set<int> us;
	for(int i = 0; i < v.size(); i++){
		if(!um[v[i]]){
			// um.insert(make_pair(a,1)); // cannot use insert again(duplicate), because !um[v[i]] will innitialize it as 0.
			um[v[i]] = 1;
			us.insert(v[i]);
		}
		else{
			um[v[i]]++;
			us.erase(v[i]);
		}
	}
	// for(auto it = um.begin();it!=um.end();it++) cout<<it->first<<" "<<it->second<<endl;
	for(auto it = us.begin();it!=us.end();it++) cout<<*it<<" ";

	return *us.begin();
}

    string longestPalindrome(string s) {
        int length = s.length();
        vector<vector<bool> > dp(length,vector<bool>(length));
        string res= "";
        int max = 0;
        
        for(int i = length - 1 ; i >= 0; i--)
            for(int j = i; j < length; j++){
                dp[i][j] = (s[i]==s[j]) && (i+2 > j || dp[i+1][j-1]);
                if(dp[i][j] && j - i >= max){
                    res = s.substr(i,j-i+1);
                    max = j - i;
                    cout<<res<<" "<<i<<" "<<j<<endl;
                }
            }
        return res;
    }
